mapper.select:
SELECT * FROM re_reple
WHERE rno = #{rno}
order by rrno asc

SELECT count(rrno)
FROM re_reple
WHERE rno = #{rno}

mapper.update:
UPDATE reple 
SET star=#{star},title=#{title},content=#{content}, rfile=#{rfile}
WHERE rno = #{rno}

UPDATE re_reple
SET content = #{content}
WHERE rrno = #{rrno}

mapper.select:
SELECT * FROM reple
WHERE nickname = #{nickname}
ORDER BY rno desc

SELECT nickname
from introduce
where ino = #{ino}
